function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    subject: "",
    message: "",
  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isFormOpen, setIsFormOpen] = useState(true);
  const [isFormExpanded, setIsFormExpanded] = useState(false);
  const [currentStep, setCurrentStep] = useState(1);
  const [isSuccess, setIsSuccess] = useState(false);
  const messageRef = useRef(null);

  useEffect(() => {
    if (isFormOpen) {
      // Small delay to allow the form to render before expanding
      setTimeout(() => {
        setIsFormExpanded(true);
      }, 10);
    } else {
      setIsFormExpanded(false);
    }
  }, [isFormOpen]);

  useEffect(() => {
    // Auto-expand textarea based on content
    if (messageRef.current) {
      messageRef.current.style.height = "auto";
      messageRef.current.style.height = messageRef.current.scrollHeight + "px";
    }
  }, [formData.message]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });

    // Real-time validation
    if (errors[name]) {
      validateField(name, value);
    }
  };

  const validateField = (name, value) => {
    let error = "";

    switch (name) {
      case "name":
        if (!value.trim()) error = "Name is required";
        else if (value.trim().length < 2)
          error = "Name must be at least 2 characters";
        break;
      case "email":
        if (!value.trim()) error = "Email is required";
        else if (!/\S+@\S+\.\S+/.test(value)) error = "Email is invalid";
        break;
      case "phone":
        if (!value.trim()) error = "Phone number is required";
        else if (!/^[0-9+\-\s()]{10,}$/.test(value))
          error = "Phone number is invalid";
        break;
      case "subject":
        if (!value.trim()) error = "Subject is required";
        else if (value.trim().length < 5)
          error = "Subject must be at least 5 characters";
        break;
      case "message":
        if (!value.trim()) error = "Message is required";
        else if (value.trim().length < 10)
          error = "Message must be at least 10 characters";
        break;
      default:
        break;
    }

    setErrors({
      ...errors,
      [name]: error,
    });

    return !error;
  };

  const validateForm = () => {
    let formErrors = {};
    let isValid = true;

    if (!validateField("name", formData.name)) isValid = false;
    if (!validateField("email", formData.email)) isValid = false;
    if (!validateField("phone", formData.phone)) isValid = false;
    if (!validateField("subject", formData.subject)) isValid = false;
    if (!validateField("message", formData.message)) isValid = false;

    return { isValid, formErrors: errors };
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { isValid } = validateForm();
    if (!isValid) {
      toast.error("Please fix the errors in the form");
      return;
    }

    setIsSubmitting(true);

    try {
      // Simulate API call
      await new Promise((resolve) => setTimeout(resolve, 1500));

      // Show success state
      setIsSuccess(true);

      // Show success notification
      toast.success(
        "Your message has been sent successfully! We will get back to you soon."
      );

      // Reset form after delay
      setTimeout(() => {
        setFormData({
          name: "",
          email: "",
          phone: "",
          subject: "",
          message: "",
        });
        setErrors({});
        setIsFormOpen(false);
        setIsSuccess(false);
        setCurrentStep(1);
      }, 3000);
    } catch (error) {
      toast.error("Something went wrong. Please try again later.");
    } finally {
      setIsSubmitting(false);
    }
  };

  const toggleForm = () => {
    if (isSuccess) return; // Don't allow closing during success animation

    setIsFormOpen(!isFormOpen);
    if (isFormOpen) {
      setIsFormExpanded(false);
    }
  };

  const handleFocus = (e) => {
    e.target.parentElement.classList.add("focused");
  };

  const handleBlur = (e) => {
    if (!e.target.value) {
      e.target.parentElement.classList.remove("focused");
    }
    validateField(e.target.name, e.target.value);
  };

  return (
    <div className="contact-page">

      <div className="contact-hero">
        <h1>Contact Us</h1>
        <p>
          Get in touch with the DCS (Department of Computer Science) at GU Ahmedabad.
          <br />
          We are here to help you with all your placement and career queries.
        </p>
      </div>

      <div className="contact-content-wrapper">
        <div className="contact-main-content">
          {/* Contact Info Section */}
          <div className="contact-info">
            <div className="contact-card">
              <h2>General Contact</h2>
              <p>
                <span className="contact-label">Address:</span> Gujarat University, Navranpura ,Ahmedabad, India
              </p>
              <p>
                <span className="contact-label">Phone:</span>{" "}
                <a href="tel:+911234567890">+91-8758129102</a>
              </p>
              <p>
                <span className="contact-label">Email:</span>{" "}
                <a href="mailto:dcs@dcs.ac.in">dcs@dcs.ac.in</a>
              </p>
              <p>
                <span className="contact-label">Website:</span>{" "}
                <a
                  href="https://www.dcs.ac.in/"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  www.dcs.ac.in
                </a>
              </p>
            </div>
          </div>

          {/* Executive Director Section */}
          <div className="executive-section">
            <h2>Leadership</h2>
            <div className="executive-card">
              <div className="photo-container">
                <img
                  src={executiveDirector.photo}
                  alt={executiveDirector.name}
                  className="executive-photo"
                />
                <div className="photo-overlay"></div>
              </div>
              <div className="executive-info">
                <h3>{executiveDirector.name}</h3>
                <p className="executive-title">{executiveDirector.title}</p>
                <div className="executive-contact">
                  <p>
                    <span className="contact-label">Phone:</span>{" "}
                    <a href={`tel:${executiveDirector.phone}`}>
                      {executiveDirector.phone}
                    </a>
                  </p>
                  <p>
                    <span className="contact-label">Email:</span>{" "}
                    <a href={`mailto:${executiveDirector.email}`}>
                      {executiveDirector.email}
                    </a>
                  </p>
                </div>
              </div>
            </div>
          </div>

          {/* DCPD Team Section */}
          <div className="team-section">
            <h2>Meet Our DCS Team</h2>
            <div className="team-grid">
              {dcpdTeam.map((member, idx) => (
                <div className="team-card" key={idx}>
                  <div className="photo-container photo-container-team">
                    <img
                      src={member.photo}
                      alt={member.name}
                      className="team-photo"
                    />
                    <div className="photo-overlay"></div>
                  </div>
                  <h3>{member.name}</h3>
                  <p className="team-title">{member.title}</p>
                  <div className="team-contact">
                    <p>
                      <span className="contact-label">Phone:</span>{" "}
                      <a href={`tel:${member.phone}`}>{member.phone}</a>
                    </p>
                    <p>
                      <span className="contact-label">Email:</span>{" "}
                      <a href={`mailto:${member.email}`}>{member.email}</a>
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Floating Contact Form */}
      <div
        className={`floating-contact-form ${isFormOpen ? "open" : ""} ${
          isFormExpanded ? "expanded" : ""
        } ${isSuccess ? "success" : ""}`}
        onClick={!isFormOpen ? toggleForm : undefined}
      >
        {!isFormOpen ? (
          <div className="floating-form-button">
            <span className="form-button-text">Contact Us</span>
            <span className="form-button-icon">âœ‰ï¸</span>
          </div>
        ) : (
          <div className="floating-form-header" onClick={toggleForm}>
            <span className="form-title">Contact Us</span>
            <span className="form-toggle-icon">
              {isSuccess ? "ğŸ‰" : "âœ•"}
            </span>
          </div>
        )}

        <div className="floating-form-content">
          {isSuccess ? (
            <div className="success-animation">
              <div className="success-checkmark">
                <div className="check-icon"></div>
              </div>
              <h3>Message Sent Successfully!</h3>
              <p>We'll get back to you shortly.</p>
              <div className="confetti">
                {[...Array(50)].map((_, i) => (
                  <div key={i} className="confetti-piece"></div>
                ))}
              </div>
            </div>
          ) : (
            <>
              <div className="form-progress">
                <div className="progress-steps">
                  {[1, 2, 3].map((step) => (
                    <div
                      key={step}
                      className={`progress-step ${
                        currentStep >= step ? "active" : ""
                      }`}
                    ></div>
                  ))}
                </div>
              </div>

              <h3>Send Us a Message</h3>
              <form className="contact-form" onSubmit={handleSubmit}>
                <div className="form-group floating">
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                    className={errors.name ? "error" : ""}
                    autoComplete="name"
                    placeholder=" "
                  />
                  <label htmlFor="name">Full Name *</label>
                  {errors.name && (
                    <span className="error-message">{errors.name}</span>
                  )}
                </div>

                <div className="form-group floating">
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                    className={errors.email ? "error" : ""}
                    autoComplete="email"
                    placeholder=" "
                  />
                  <label htmlFor="email">Email Address *</label>
                  {errors.email && (
                    <span className="error-message">{errors.email}</span>
                  )}
                </div>

                <div className="form-group floating">
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                    className={errors.phone ? "error" : ""}
                    autoComplete="tel"
                    placeholder=" "
                  />
                  <label htmlFor="phone">Phone Number *</label>
                  {errors.phone && (
                    <span className="error-message">{errors.phone}</span>
                  )}
                </div>

                <div className="form-group floating">
                  <input
                    type="text"
                    id="subject"
                    name="subject"
                    value={formData.subject}
                    onChange={handleChange}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                    className={errors.subject ? "error" : ""}
                    placeholder=" "
                  />
                  <label htmlFor="subject">Subject *</label>
                  {errors.subject && (
                    <span className="error-message">{errors.subject}</span>
                  )}
                </div>

                <div className="form-group floating">
                  <textarea
                    id="message"
                    name="message"
                    value={formData.message}
                    onChange={handleChange}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                    className={errors.message ? "error" : ""}
                    ref={messageRef}
                    rows="3"
                    placeholder=" "
                  ></textarea>
                  <label htmlFor="message">Message *</label>
                  <div className="character-count">
                    {formData.message.length}/500
                  </div>
                  {errors.message && (
                    <span className="error-message">{errors.message}</span>
                  )}
                </div>

                <button
                  type="submit"
                  className="submit-bttn"
                  disabled={isSubmitting}
                >
                  {isSubmitting ? (
                    <>
                      <span className="spinner"></span>
                      Sending...
                    </>
                  ) : (
                    "Send Message"
                  )}
                </button>
              </form>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default Contact;

