  font-size: 0.75rem;
  font-weight: 600;
}

.tag-urgent { background-color: #fecaca; color: #dc2626; }
.tag-scheduled { background-color: #dbeafe; color: #1e40af; }

.schedule-title {
  font-weight: 600;
  color: var(--text-primary);
}

.schedule-info {
  font-size: 0.875rem;
  color: var(--text-secondary);
}

.schedule-meta-details {
  display: flex;
  align-items: center;
  gap: 1rem;
}

.meta-item {
  display: flex;
  align-items: center;
  gap: 0.25rem;
  font-size: 0.875rem;
  color: var(--text-muted);
}

/* Responsive Design */
@media (max-width: 1200px) {
  .main-content {
    padding: 1.5rem;
  }
}

@media (max-width: 768px) {
  .main-content {
    padding: 1rem;
  }
  .header-content {
    padding: 0 1rem;
  }
  .welcome-card, .profile-overview-card, .quick-action-card, .chart-card, .application-card, .job-card, .schedule-card {
    padding: 1rem;
  }
  .profile-overview-header {
    flex-direction: column;
    text-align: center;
  }
  .profile-stats {
    flex-wrap: wrap;
    justify-content: center;
    gap: 1rem;
  }
  .quick-actions-grid, .charts-grid, .stats-grid, .applications-grid, .jobs-grid, .schedule-list {
    grid-template-columns: 1fr;
  }
  .schedule-card, .application-card .card-header {
    flex-direction: column;
    align-items: center;
    text-align: center;
    gap: 1rem;
  }
  .card-actions-wrapper {
    flex-direction: column;
    gap: 0.5rem;
  }
  .rounds-list {
    justify-content: center;
  }
}

frontend/src/components/ThemeToggle.jsx.content:
import React from 'react';
import { Moon, Sun } from 'lucide-react';
import { useDarkMode } from '../hooks/useDarkMode';

export default function ThemeToggle({ className = '' }) {
  const { theme, toggle } = useDarkMode();
  const isLight = theme === 'light';
  return (
    <button
      onClick={toggle}
      aria-label="Toggle light/dark theme"
      className={`inline-flex items-center justify-center h-9 w-9 rounded-md border border-[var(--color-border)] bg-[var(--color-surface)] text-[var(--color-text)] shadow-[var(--elev-1)] hover:shadow-[var(--elev-2)] hover:bg-[var(--color-bg-muted)] transition ${className}`}
    >
      {isLight ? <Moon size={18} /> : <Sun size={18} />}
    </button>
  );
}

