        </div>        <div className="job-section">
          <h3>Requirements</h3>
          <ul className="requirements-list">
            <li className="requirement-item">
              <span className="bullet">â€¢</span> Check eligibility criteria above for course, year, and branch requirements
            </li>
            <li className="requirement-item">
              <span className="bullet">â€¢</span> Good academic record and relevant skills as mentioned
            </li>
          </ul>
        </div>
        {skills.length > 0 && (
          <div className="job-section">
            <h3>Skills</h3>
            <div className="skills-container">
              {skills.map((skill, index) => (
                <span key={index} className="skill-tag">{skill}</span>
              ))}
            </div>
          </div>
        )}
        <div className="job-section">
          <h3>Benefits</h3>
          <ul className="benefits-list">
            {benefits.length > 0 ? benefits.map((benefit, index) => (
              <li key={index} className="benefit-item">
                <span className="bullet">â€¢</span> {benefit}
              </li>
            )) : (
              <li className="benefit-item">
                <span className="bullet">â€¢</span> Benefits information not provided.
              </li>
            )}
          </ul>
        </div>        {job.companyWebsite ? (
          <a 
            href={job.companyWebsite} 
            target="_blank" 
            rel="noopener noreferrer" 
            className="apply-button"
          >
            Apply Now
          </a>
        ) : (
          <button className="apply-button" onClick={() => onApply && onApply(job)}>
            Apply Now
          </button>
        )}
      </div>
    </div>
  );
};

// Main Jobs Component
const Jobs = () => {
  const [selectedJob, setSelectedJob] = useState(null);
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  // Fetch jobs from backend
  useEffect(() => {
    const fetchJobs = async () => {
      try {
        setLoading(true);
        const response = await fetch(API_ENDPOINTS.JOBS);
        if (!response.ok) {
          throw new Error('Failed to fetch jobs');
        }
        const jobsData = await response.json();
        console.log('Fetched jobs data:', jobsData); // Debug log
        setJobs(jobsData);
      } catch (err) {
        setError(err.message);
        console.error('Error fetching jobs:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchJobs();
  }, []);

  const handleJobClick = (job) => {
    setSelectedJob(job);
  };

  const handleBack = () => {
    setSelectedJob(null);
  };

  const handleApply = (job) => {
    alert(`Applied for ${job.position} at ${job.companyName}`);
  };

  if (loading) {
    return (
      <div className="jobs-page-container">
        <div className="loading-container">
          <h2>Loading jobs...</h2>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="jobs-page-container">
        <div className="error-container">
          <h2>Error loading jobs</h2>
          <p>{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="jobs-page-container">
      {!selectedJob ? (
        <div>
          <div className="jobs-header">
            <h1 className="jobs-title">Available Jobs</h1>
            <p className="jobs-subtitle">{jobs.length} job(s) available</p>
          </div>
          <div className="jobs-list">
            {jobs.length === 0 ? (
              <div className="no-jobs-container">
                <h3>No jobs available at the moment</h3>
                <p>Please check back later for new opportunities.</p>
              </div>
            ) : (
              jobs.map((job) => (
                <JobCard key={job._id} job={job} onClick={handleJobClick} />
              ))
            )}
          </div>
        </div>
      ) : (
        <JobDetails 
          job={selectedJob} 
          onBack={handleBack} 
          onApply={handleApply} 
        />
      )}
    </div>
  );
};

export default Jobs;

