                  placeholder="JavaScript, React, Node.js, Python..."
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Certifications (comma separated)</label>
                <input
                  type="text"
                  value={profileEditData.certifications.join(', ')}
